package uu.jazy.core ;/** * Lazy and Functional. * Package for laziness and functions as known from functional languages. * Written by Atze Dijkstra, atze@cs.uu.nl * * $Header:     $ * $Archive:    $ * $NoKeywords: $ *//** * A value which does not need any parameters, but has to be evaluated before used. * Used to build CAF's (Constant Applicative Forms). */public class Apply0 extends Apply{	public Apply0( )	{	    super( null ) ;	}		public Apply0( Object f )	{		super( f ) ;	}	    public Object[] getBoundParams()    {	    return Utils.zeroArray ;    }    public int getNrBoundParams()    {        return 0 ;    }    protected void eraseRefs()    {    }    }